import React, { Component } from 'react'

export default class Timer extends Component {

  constructor(props) {
    super(props)
  
    this.state = {
       hideTimer: false,
       time: 0,
       startTime: Date.now()
    }
  }
  
  componentDidMount(){
    let timer = setInterval(this.tick.bind(this), 1000);
    this.setState({time: Date.now});
  }

  tick() {
    this.setState({
      time: 1
    });
    console.log(this.state);
  }
  
  render() {
    const { hideTimer, timer } = this.state;
    return (
      <div className={hideTimer ? '.d-none' : ''}>
        <h4>{timer}</h4>
        <div>
          Too much pressure ? <br />
          <button className="btn btn-dark btn-sm mr-auto">Hide Timer</button>
        </div>
      </div>
    )
  }
}
