import React, { Component } from 'react'
import axios from 'axios';
import { Container, Row, Col } from 'reactstrap';
import BoardRow from './BoardRow';

export default class Sudoku extends Component {
  constructor(props) {
    super(props)
  
    this.state = {
       boardRows: [0, 1, 2, 3, 4, 5, 6, 7, 8],
       initialBoard: [],
       finalBoard: [],
       level: null
    }
  }

  componentWillMount(){
    const { id } = this.props.match.params;
    console.log(id);
    axios
    .get(`/api/sudokus/${id}`)
    .then(res => {
      this.setState({
        initialBoard: res.data.initialBoard,
        finalBoard:  res.data.finalBoard,
        level:  res.data.level
      })
    })
  }
  

  render() {
    const { boardRows, fields, boardResults } = this.state;
    return (
      <Container>
        <Row>
          <Col md="2"></Col>
          <Col md="8">
          <table className="sudoku-board">
              {boardRows.map(boardRow => (
                <BoardRow key={boardRow} row={boardRow} fields={initialBoard} boardResults={finalBoard} />
              ))}
            </table>
          </Col>
          <Col md="2"></Col>
        </Row>
        <h1>Sudoku</h1>

      </Container>
    )
  }
}
