import React, { Component } from 'react'

export default class Timer extends Component {

  constructor(props) {
    super(props)
  
    this.state = {
       hideTimer: false,
       secs: 55,
       mins: 59,
       hours: 1,
       startTime: Date.now()
    }
  }
  
  componentDidMount(){
    setInterval(this.tick.bind(this), 1000);
  }

  tick() {
    const { secs, mins, hours } = this.state;
    let newSecs = secs + 1;
    let newMins = mins;
    let newHours = hours;
    if (newSecs === 60){
      newSecs = 0;
      newMins = mins + 1;
      if (newMins === 60){
        newSecs = 0;
        newMins = 0;
        newHours = hours + 1;
      }
    }

    this.setState({
      secs: newSecs,
      mins: newMins,
      hours: newHours
    });

  }
  
  render() {
    const { hideTimer, secs, mins, hours } = this.state;
    return (
      <div className={hideTimer ? '.d-none' : ''}>
        <h4>{hours}:{mins}:{secs}</h4>
        <div>
          Too much pressure ? <br />
          <button className="btn btn-dark btn-sm mr-auto">Hide Timer</button>
        </div>
      </div>
    )
  }
}
